declare module "redis-typescript" {
  export function createClient(options: Options): RedisClient;
}

interface Options {
  host: string;
  port: number;
  password?: string;
}

interface RedisClient {
  // other
  on(event: string, listener: (...args: any[]) => void): void;
  expire(key: string, seconds: number): Promise<void>;

  // string
  set(key: string, value: string): Promise<any>;
  get(key: string): Promise<any>;
  exists(key: string): Promise<boolean>;

  // hash
  hset(key: string, field: string, value: string): Promise<any>;
  hmset(key: string, object: object): Promise<any>;
  hget(key: string, field: string): Promise<any>;
  hexists(key: string, field: string): Promise<any>;
}
